import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import '../styles/MedicationPage.css';

function MedicationPage() {
  const [medications, setMedications] = useState([]);
  const [newMedication, setNewMedication] = useState({
    name: '',
    dosage: '',
    frequency: '',
    startDate: '',
    endDate: '',
    prescribedBy: '',
    notes: ''
  });
  const [editingId, setEditingId] = useState(null);
  const [filter, setFilter] = useState('current'); // 'all', 'current', 'past'

  // Mock data loading
  useEffect(() => {
    // Fetch medications
    setMedications([
      { 
        id: '1', 
        name: 'Lisinopril', 
        dosage: '10mg', 
        frequency: 'Once daily', 
        startDate: '2024-10-15', 
        endDate: '', 
        prescribedBy: 'Dr. Robert Chen',
        notes: 'Take in the morning with food'
      },
      { 
        id: '2', 
        name: 'Vitamin D', 
        dosage: '1000 IU', 
        frequency: 'Once daily', 
        startDate: '2024-11-01', 
        endDate: '', 
        prescribedBy: 'Dr. Sarah Williams',
        notes: 'Take with dinner'
      },
      { 
        id: '3', 
        name: 'Metformin', 
        dosage: '500mg', 
        frequency: 'Twice daily', 
        startDate: '2024-09-10', 
        endDate: '', 
        prescribedBy: 'Dr. Robert Chen',
        notes: 'Take with meals'
      },
      { 
        id: '4', 
        name: 'Prednisone', 
        dosage: '5mg', 
        frequency: 'Once daily for 10 days', 
        startDate: '2024-12-05', 
        endDate: '2024-12-15', 
        prescribedBy: 'Dr. James Wilson',
        notes: 'Short course for inflammation'
      }
    ]);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewMedication({
      ...newMedication,
      [name]: value
    });
  };

  const handleAddMedication = (e) => {
    e.preventDefault();

    if (editingId) {
      // Update existing medication
      setMedications(medications.map(med => 
        med.id === editingId ? { ...newMedication, id: editingId } : med
      ));
      setEditingId(null);
    } else {
      // Create a new medication
      const medication = {
        id: Date.now().toString(), // temporary ID for mock data
        ...newMedication
      };

      // Add to the medications array
      setMedications([...medications, medication]);
    }

    // Reset form
    setNewMedication({
      name: '',
      dosage: '',
      frequency: '',
      startDate: '',
      endDate: '',
      prescribedBy: '',
      notes: ''
    });
  };

  const handleEdit = (medication) => {
    setNewMedication({ ...medication });
    setEditingId(medication.id);

    // Scroll to form
    document.querySelector('.add-medication-section').scrollIntoView({ 
      behavior: 'smooth' 
    });
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to remove this medication?')) {
      setMedications(medications.filter(med => med.id !== id));

      if (editingId === id) {
        setEditingId(null);
        setNewMedication({
          name: '',
          dosage: '',
          frequency: '',
          startDate: '',
          endDate: '',
          prescribedBy: '',
          notes: ''
        });
      }
    }
  };

  const filterMedications = () => {
    const today = new Date().toISOString().split('T')[0];

    switch (filter) {
      case 'current':
        return medications.filter(med => !med.endDate || med.endDate >= today);
      case 'past':
        return medications.filter(med => med.endDate && med.endDate < today);
      default:
        return medications;
    }
  };

  return (
    <div className="medication-page">
      <Header user={{ name: 'Jane Doe' }} onLogout={() => console.log('logout')} />

      <main>
        <h2>Medications</h2>

        <div className="filter-controls">
          <button 
            className={filter === 'all' ? 'active' : ''} 
            onClick={() => setFilter('all')}
          >
            All Medications
          </button>
          <button 
            className={filter === 'current' ? 'active' : ''} 
            onClick={() => setFilter('current')}
          >
            Current Medications
          </button>
          <button 
            className={filter === 'past' ? 'active' : ''} 
            onClick={() => setFilter('past')}
          >
            Past Medications
          </button>
        </div>

        <div className="medications-list">
          {filterMedications().length === 0 ? (
            <p className="no-medications">No medications found</p>
          ) : (
            <table className="medications-table">
              <thead>
                <tr>
                  <th>Medication</th>
                  <th>Dosage</th>
                  <th>Frequency</th>
                  <th>Start Date</th>
                  <th>End Date</th>
                  <th>Prescribed By</th>
                  <th>Notes</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filterMedications().map(med => (
                  <tr key={med.id}>
                    <td>{med.name}</td>
                    <td>{med.dosage}</td>
                    <td>{med.frequency}</td>
                    <td>{med.startDate}</td>
                    <td>{med.endDate || 'Ongoing'}</td>
                    <td>{med.prescribedBy}</td>
                    <td>{med.notes}</td>
                    <td>
                      <button 
                        className="edit-btn" 
                        onClick={() => handleEdit(med)}
                      >
                        Edit
                      </button>
                      <button 
                        className="delete-btn" 
                        onClick={() => handleDelete(med.id)}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>

        <div className="add-medication-section">
          <h3>{editingId ? 'Edit Medication' : 'Add New Medication'}</h3>
          <form onSubmit={handleAddMedication}>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="name">Medication Name</label>
                <input 
                  type="text" 
                  id="name" 
                  name="name" 
                  value={newMedication.name} 
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="dosage">Dosage</label>
                <input 
                  type="text" 
                  id="dosage" 
                  name="dosage" 
                  value={newMedication.dosage} 
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="frequency">Frequency</label>
                <input 
                  type="text" 
                  id="frequency" 
                  name="frequency" 
                  value={newMedication.frequency} 
                  onChange={handleInputChange}
                  required
                  placeholder="e.g., Once daily, Twice daily"
                />
              </div>

              <div className="form-group">
                <label htmlFor="prescribedBy">Prescribed By</label>
                <input 
                  type="text" 
                  id="prescribedBy" 
                  name="prescribedBy" 
                  value={newMedication.prescribedBy} 
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="startDate">Start Date</label>
                <input 
                  type="date" 
                  id="startDate" 
                  name="startDate" 
                  value={newMedication.startDate} 
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="endDate">End Date (leave blank if ongoing)</label>
                <input 
                  type="date" 
                  id="endDate" 
                  name="endDate" 
                  value={newMedication.endDate} 
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="notes">Notes</label>
              <textarea 
                id="notes" 
                name="notes" 
                value={newMedication.notes} 
                onChange={handleInputChange}
                rows="3"
                placeholder="Special instructions, side effects to watch, etc."
              ></textarea>
            </div>

            <div className="form-actions">
              <button type="submit" className="save-btn">
                {editingId ? 'Update Medication' : 'Add Medication'}
              </button>

              {editingId && (
                <button 
                  type="button" 
                  className="cancel-btn" 
                  onClick={() => {
                    setEditingId(null);
                    setNewMedication({
                      name: '',
                      dosage: '',
                      frequency: '',
                      startDate: '',
                      endDate: '',
                      prescribedBy: '',
                      notes: ''
                    });
                  }}
                >
                  Cancel Edit
                </button>
              )}
            </div>
          </form>
        </div>
      </main>
    </div>
  );
}

export default MedicationPage;
