import React, { useState } from 'react';
import {
  Container,
  Box,
  Typography,
  Card,
  CardContent,
  Avatar,
  Grid,
  TextField,
  Button,
  Divider,
  Paper,
  Chip,
  IconButton,
  Alert,
  Snackbar,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';
import Header from '../components/Header';
import CameraAltIcon from '@mui/icons-material/CameraAlt';
import EditIcon from '@mui/icons-material/Edit';
import SaveIcon from '@mui/icons-material/Save';
import LocalPhoneIcon from '@mui/icons-material/LocalPhone';
import EmailIcon from '@mui/icons-material/Email';
import HomeIcon from '@mui/icons-material/Home';
import BadgeIcon from '@mui/icons-material/Badge';
import PersonIcon from '@mui/icons-material/Person';

function ProfilePage() {
  // Mock user data
  const [userData, setUserData] = useState({
    name: 'Ari Berkowitz',
    email: 'rabbib@example.com',
    phone: '(555) 123-4567',
    role: 'admin',
    address: '123 Main St, New York, NY 10001',
    bio: 'Family coordinator and primary caregiver responsible for managing patient care and coordination.',
    dateJoined: 'January 2023',
    preferredContactMethod: 'email'
  });

  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState(userData);
  const [openSnackbar, setOpenSnackbar] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setUserData(formData);
    setEditMode(false);
    setOpenSnackbar(true);
  };

  const handleCancel = () => {
    setFormData(userData);
    setEditMode(false);
  };

  const closeSnackbar = () => {
    setOpenSnackbar(false);
  };

  return (
    <>
      <Header />
      <Container maxWidth="lg" sx={{ mt: 4, mb: 8 }}>
        <Typography variant="h4" component="h1" fontWeight="bold" sx={{ mb: 4 }}>
          My Profile
        </Typography>

        <Grid container spacing={4}>
          {/* Profile Card */}
          <Grid item xs={12} md={4}>
            <Card elevation={2}>
              <CardContent sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', py: 4 }}>
                <Box sx={{ position: 'relative' }}>
                  <Avatar
                    src="/images/rabbiB.png"
                    alt="Ari Berkowitz"
                    sx={{ width: 160, height: 160, mb: 2, border: '4px solid white', boxShadow: '0 4px 10px rgba(0,0,0,0.1)' }}
                  />
                  <IconButton 
                    color="primary" 
                    sx={{ 
                      position: 'absolute',
                      bottom: 10,
                      right: 0,
                      bgcolor: 'white',
                      boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
                      '&:hover': {
                        bgcolor: 'white',
                      }
                    }}
                  >
                    <CameraAltIcon />
                  </IconButton>
                </Box>
                <Typography variant="h5" component="h2" fontWeight="bold">
                  {userData.name}
                </Typography>
                <Chip 
                  label={userData.role === 'admin' ? 'Administrator' : 'Family Member'} 
                  color="primary" 
                  sx={{ mt: 1, mb: 2 }}
                />
                <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center' }}>
                  {userData.bio}
                </Typography>
                <Divider sx={{ width: '100%', my: 3 }} />
                <Box sx={{ width: '100%' }}>
                  <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                    <EmailIcon fontSize="small" sx={{ verticalAlign: 'middle', mr: 1 }} />
                    {userData.email}
                  </Typography>
                  <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                    <LocalPhoneIcon fontSize="small" sx={{ verticalAlign: 'middle', mr: 1 }} />
                    {userData.phone}
                  </Typography>
                  <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                    <HomeIcon fontSize="small" sx={{ verticalAlign: 'middle', mr: 1 }} />
                    {userData.address}
                  </Typography>
                  <Typography variant="subtitle2" color="text.secondary" sx={{ mt: 2 }}>
                    Member since {userData.dateJoined}
                  </Typography>
                </Box>
              </CardContent>
            </Card>
          </Grid>

          {/* Profile Edit Form */}
          <Grid item xs={12} md={8}>
            <Paper elevation={2} sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Typography variant="h5" component="h2" fontWeight="bold">
                  {editMode ? 'Edit Profile' : 'Profile Information'}
                </Typography>
                {!editMode && (
                  <Button 
                    startIcon={<EditIcon />} 
                    variant="outlined" 
                    onClick={() => setEditMode(true)}
                  >
                    Edit
                  </Button>
                )}
              </Box>

              <Divider sx={{ mb: 3 }} />

              <form onSubmit={handleSubmit}>
                <Grid container spacing={3}>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Full Name"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      disabled={!editMode}
                      InputProps={{
                        startAdornment: <PersonIcon color="action" sx={{ mr: 1 }} />,
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Email"
                      name="email"
                      type="email"
                      value={formData.email}
                      onChange={handleChange}
                      disabled={!editMode}
                      InputProps={{
                        startAdornment: <EmailIcon color="action" sx={{ mr: 1 }} />,
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Phone"
                      name="phone"
                      value={formData.phone}
                      onChange={handleChange}
                      disabled={!editMode}
                      InputProps={{
                        startAdornment: <LocalPhoneIcon color="action" sx={{ mr: 1 }} />,
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <FormControl fullWidth disabled={!editMode}>
                      <InputLabel id="role-label">Role</InputLabel>
                      <Select
                        labelId="role-label"
                        name="role"
                        value={formData.role}
                        label="Role"
                        onChange={handleChange}
                        startAdornment={<BadgeIcon color="action" sx={{ mr: 1 }} />}
                      >
                        <MenuItem value="admin">Administrator</MenuItem>
                        <MenuItem value="caregiver">Primary Caregiver</MenuItem>
                        <MenuItem value="family">Family Member</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Address"
                      name="address"
                      value={formData.address}
                      onChange={handleChange}
                      disabled={!editMode}
                      InputProps={{
                        startAdornment: <HomeIcon color="action" sx={{ mr: 1 }} />,
                      }}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Bio"
                      name="bio"
                      value={formData.bio}
                      onChange={handleChange}
                      disabled={!editMode}
                      multiline
                      rows={4}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <FormControl fullWidth disabled={!editMode}>
                      <InputLabel id="contact-method-label">Preferred Contact Method</InputLabel>
                      <Select
                        labelId="contact-method-label"
                        name="preferredContactMethod"
                        value={formData.preferredContactMethod}
                        label="Preferred Contact Method"
                        onChange={handleChange}
                      >
                        <MenuItem value="email">Email</MenuItem>
                        <MenuItem value="phone">Phone</MenuItem>
                        <MenuItem value="text">Text Message</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>

                  {editMode && (
                    <Grid item xs={12} sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2, mt: 2 }}>
                      <Button variant="outlined" onClick={handleCancel}>
                        Cancel
                      </Button>
                      <Button 
                        type="submit" 
                        variant="contained" 
                        color="primary" 
                        startIcon={<SaveIcon />}
                      >
                        Save Changes
                      </Button>
                    </Grid>
                  )}
                </Grid>
              </form>
            </Paper>
          </Grid>
        </Grid>
      </Container>

      <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={closeSnackbar}>
        <Alert onClose={closeSnackbar} severity="success" sx={{ width: '100%' }}>
          Profile updated successfully!
        </Alert>
      </Snackbar>
    </>
  );
}

export default ProfilePage;
